def effacer_console
    if Gem.win_platform?
      system("cls")
    else
      system("clear")
    end
  end


loop do
    puts ""
    puts ""
    puts "BIG DATA:"
    puts "1. Combien y a-t-il de handle dans cette array ?"
    puts "2. Quel est le handle le plus court de cette liste ?"
    puts "3. Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)"
    puts "4. Combien commencent par une majuscule (première lettre juste après le @) ?"
    puts "5. Trie la liste de handle par ordre alphabétique."
    puts "6. Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)"
    puts "7. Quelle est la position dans l'array de la personne @epenser ?"
    puts "8. Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)"
    puts "0. Quitter"
  
    print "Entrez votre choix (1-8) ou 0 pour quitter: "
    choix = gets.chomp.to_i


    case choix
    when 1
        effacer_console
      # 1. Combien y a-t-il de handle dans cette array ?
        puts yellow_bold ("Le tableau est composé de #{twitter.length} élements !")

    when 2
      # 2. Quel est le handle le plus court de cette liste ?
        effacer_console
        handle_shorter  = twitter.min_by {|handle| handle.length }
        puts yellow_bold ("L'élement le plus court est: #{handle_shorter}.")

    when 3
      # 3. Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
        # ici on utilise select -> parcours l'ensemble du tableau et ne conserve que ceux dont la longueur = 6 (5 caracteres sans le @)
        # la méthode count compte le nombre d'éléments dans le nouvel array
        effacer_console
        number_handles_5_caracteres     = twitter.select { |handle| handle.length == 6 }.count
        puts yellow_bold ("Il y a: #{number_handles_5_caracteres} éléments composés de 5 caractères.")

      
    when 4
      # 4. Combien commencent par une majuscule (première lettre juste après le @) ?
        # on commence a compter a partir de l'indice 1 poiur exclure le @
        effacer_console
        number_handles_uppercase = twitter.select { |handle| handle[1].match?(/[A-Z]/) }.count
        puts yellow_bold ("Il y a: #{number_handles_uppercase} éléments qui commencent par une majuscule.")

      
    when 5
      # 5. Trie la liste de handle par ordre alphabétique.
        # sort trie les éléments de l'array twitter par ordre alphabétique
        effacer_console
        twitter_sort = twitter.sort
        puts yellow_bold ("Voici votre tableau trié par ordre alphabétique:")
        puts ""
        puts twitter_sort

      
    when 6
      # 6. Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)*
        effacer_console
        twitter_sort_by_length = twitter.sort_by { |handle| handle.length }
        puts yellow_bold ("Voici les éléments de votre tableau trié par longueur, les plus petits avant, les plus grands en dernier:")
        puts ""
        puts twitter_sort_by_length

      
    when 7
      # 7. Quelle est la position dans l'array de la personne @epenser ?
        effacer_console
        position_epenser = twitter.index("@epenser")
        puts yellow_bold ("L'élement @epenser se trouve à la #{position_epenser} ème position dans le tableau")
      
    when 8
      # 8. Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
        # ici on utilise group_by => regroupe les éléments en fonction de leurs tailles sans compter le @ (length - 1)
        # on utilise ensuite transform_values => remplace les valeurs par leur nombre d'élements respectifs en utilisant count
        repartition_by_size = twitter.group_by { |handle| handle.length - 1 }.transform_values(&:count)

        # ici on utilise sort_by en premier afin de trier les handles en fonction de leurs tailles
        # puis on utilise each pour voir ce qu'il y a dans ce tableau trié par taille et renvoyer le résultat demandé
        # length => clef (taille de caractères) && _ => valeur (nombre d'éléments pour chacune de ces tailles)
        effacer_console
        repartition_by_size.sort_by{ |length, _| length}.each do |length, number|
            puts yellow_bold ("Il y a #{number} éléments composés de #{length} caractères")
        end

    when 0
        effacer_console
        puts yellow_bold ("Allez ciao !")
        puts ""
        break
    else
        puts ""
      puts red_bold("Choix invalide. Veuillez choisir une option entre 1 et 9, ou 0 pour quitter.")
    end
  
    puts "\n"
  end